import React, { useState } from 'react';
import { generateQuestions, saveAnswer, evaluateAnswer } from '../api/api';

export default function InterviewPage() {
  const [company, setCompany] = useState('');
  const [role, setRole] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answerText, setAnswerText] = useState('');
  const [evaluation, setEvaluation] = useState(null);

  const handleGenerate = async () => {
    const r = await generateQuestions({ user_id: 1, company, role });
    setQuestions(r.data.questions);
  };

  const handleSave = async (idx) => {
    const qid = idx + 1;
    const r = await saveAnswer({ question_id: qid, answer_text: answerText });
    alert('Saved answer id ' + r.data.id);
  };

  const handleEval = async (aid) => {
    const r = await evaluateAnswer(aid, { answer: answerText });
    setEvaluation(r.data);
  };

  return (
    <div>
      <h2>Interview Preparation</h2>
      Company: <input value={company} onChange={e => setCompany(e.target.value)} />{' '}
      Role: <input value={role} onChange={e => setRole(e.target.value)} />{' '}
      <button onClick={handleGenerate}>Generate Questions</button>
      <ul>
        {questions.map((q, i) => (
          <li key={i}>
            {q}{' '}
            <button onClick={() => handleSave(i)}>Save Answer</button>{' '}
            <button onClick={() => handleEval(i + 1)}>Evaluate Answer</button>
          </li>
        ))}
      </ul>

      {evaluation && (
        <div>
          <h3>Evaluation</h3>
          Score: {evaluation.score}
          <br />
          Feedback: {evaluation.feedback}
        </div>
      )}
    </div>
  );
}
