// src/pages/InterviewPage.jsx
import React, { useState } from 'react';
import {
  generateQuestions,    // POST /interviews/questions
  saveAnswer,           // POST /interviews/answers
  evaluateAnswer        // POST /interviews/evaluate/{answer_id}
} from '../api/api';

export default function InterviewPage() {
  const [company, setCompany] = useState('');
  const [role, setRole] = useState('');
  const [questions, setQuestions] = useState([]);

  const handleGenerate = async () => {
    if (!company.trim() || !role.trim()) {
      alert('CompanyÏôÄ RoleÏùÑ Î™®Îëê ÏûÖÎ†•ÌïòÏÑ∏Ïöî.');
      return;
    }
    try {
      const res = await generateQuestions({ user_id: 1, company, role });
      // APIÎ°úÎ∂ÄÌÑ∞ Î∞õÏùÄ [{id, text}]Î•º
      // UIÏö©ÏúºÎ°ú ÌôïÏû•Îêú Í∞ùÏ≤¥Î°ú Î≥ÄÌôò
      const formatted = (res.data.questions || []).map((q) => ({
        id: q.id,
        text: q.text ?? q.question ?? q, // Ïú†Ïó∞ÌïòÍ≤å Ï≤òÎ¶¨
        userAnswer: '',
        answerSaved: false,
        answerId: null,
        evaluation: null,
      }));
      setQuestions(formatted);
    } catch (err) {
      console.error('‚ùå ÏßàÎ¨∏ ÏÉùÏÑ± Ïã§Ìå®', err);
      alert('ÏßàÎ¨∏ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const handleSave = async (q, idx) => {
    if (!q.userAnswer.trim()) {
      alert('Î®ºÏ†Ä ÎãµÎ≥ÄÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî.');
      return;
    }
    try {
      const payload = {
        question_id: q.id,
        answer_text: q.userAnswer.trim(),
      };
      const res = await saveAnswer(payload);
      const updated = [...questions];
      updated[idx] = {
        ...q,
        answerSaved: true,
        answerId: res.data.id,
      };
      setQuestions(updated);
    } catch (err) {
      console.error('‚ùå ÎãµÎ≥Ä Ï†ÄÏû• Ïã§Ìå®', err);
      alert('ÎãµÎ≥Ä Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const handleEvaluate = async (q, idx) => {
    if (!q.answerSaved) {
      alert('Î®ºÏ†Ä Save Î≤ÑÌäºÏùÑ ÎàåÎü¨Ï£ºÏÑ∏Ïöî.');
      return;
    }
    try {
      const res = await evaluateAnswer(q.answerId, { answer_text: q.userAnswer });
      const updated = [...questions];
      updated[idx] = {
        ...q,
        evaluation: res.data,
      };
      setQuestions(updated);
    } catch (err) {
      console.error('‚ùå ÌèâÍ∞Ä Ïã§Ìå®', err);
      alert('ÌèâÍ∞ÄÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="container py-4">
      <h2 className="mb-4">üé§ Î™®Ïùò Î©¥Ï†ë</h2>

      {/* Input row */}
      <div className="row g-3 mb-5">
        <div className="col-md-5">
          <input
            type="text"
            className="form-control shadow-sm"
            placeholder="ÏßÄÏõêÌï† ÌöåÏÇ¨"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
          />
        </div>
        <div className="col-md-5">
          <input
            type="text"
            className="form-control shadow-sm"
            placeholder="ÏßÄÏõêÌï† ÏßÅÎ¨¥"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          />
        </div>
        <div className="col-md-2">
          <button
            className="btn btn-success w-100"
            onClick={handleGenerate}
          >
            ÏßàÎ¨∏ÏÉùÏÑ±
          </button>
        </div>
      </div>

      {/* Questions list */}
      <ul className="list-group mb-4 shadow-sm">
        {questions.length === 0 && (
          <li className="list-group-item text-muted">
            ÏßàÎ¨∏Ïù¥ ÏóÜÏäµÎãàÎã§. GenerateÎ•º ÎàåÎü¨Ï£ºÏÑ∏Ïöî.
          </li>
        )}
        {questions.map((q, idx) => (
          <li key={q.id} className="list-group-item">
            <p className="mb-2"><strong>Q:</strong> {q.text}</p>
            <textarea
              className="form-control mb-3"
              rows={3}
              value={q.userAnswer}
              onChange={(e) => {
                const updated = [...questions];
                updated[idx] = { ...q, userAnswer: e.target.value };
                setQuestions(updated);
              }}
            />
            <div className="d-flex gap-2">
              <button
                className={`btn btn-outline-primary btn-sm${q.answerSaved ? ' disabled' : ''}`}
                onClick={() => handleSave(q, idx)}
              >
                {q.answerSaved ? 'Saved' : 'Save'}
              </button>
              <button
                className="btn btn-outline-info btn-sm"
                onClick={() => handleEvaluate(q, idx)}
              >
                Evaluate
              </button>
            </div>
            {q.evaluation && (
              <div className="mt-3 p-3 border rounded bg-light">
                <h6>üìù Evaluation</h6>
                <p className="mb-1"><strong>Score:</strong> {q.evaluation.score}</p>
                <p className="mb-0"><strong>Feedback:</strong> {q.evaluation.feedback}</p>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
