import React, { useState } from 'react';
import {
  requestExportDocx,
  requestExportPdf,
  downloadDocx,
  downloadPdf,
} from '../api/api';

export default function ExportPage() {
  const [loading, setLoading] = useState(false);

  const handleExport = async (type) => {
    setLoading(true);
    try {
      // 1) Î¶¨Ìè¨Ìä∏ ÏÉùÏÑ± ‚Üí filename Î∞õÍ∏∞
      const exportRes =
        type === 'docx'
          ? await requestExportDocx(1)
          : await requestExportPdf(1);
      const { filename } = exportRes.data;

      // 2) Ïã§Ï†ú ÌååÏùº Îã§Ïö¥Î°úÎìú (blob)
      const fileRes =
        type === 'docx'
          ? await downloadDocx(filename)
          : await downloadPdf(filename);

      const blob = new Blob([fileRes.data], {
        type: fileRes.headers['content-type'],
      });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  return (
    <div>
      <h2 className="mb-4">üì• Ï∑®Ï§Ä ÌûàÏä§ÌÜ†Î¶¨ Îã§Ïö¥Î°úÎìú</h2>

      <div className="row g-3 mb-5">
        <div className="col-md-5">
          <button
            className="btn btn-outline-primary w-100"
            disabled={loading}
            onClick={() => handleExport('docx')}
          >
            DOCXÎ°ú Îã§Ïö¥Î°úÎìú
          </button>
        </div>
        <div className="col-md-5">
          <button
            className="btn btn-outline-success w-100"
            disabled={loading}
            onClick={() => handleExport('pdf')}
          >
            PDFÎ°ú Îã§Ïö¥Î°úÎìú 
          </button>
        </div>
        <div className="col-md-2 d-flex align-items-center">
          {loading && (
            <span className="text-muted">ÌååÏùº ÏÉùÏÑ±/Îã§Ïö¥Î°úÎìú Ï§ë‚Ä¶</span>
          )}
        </div>
      </div>
    </div>
  );
}
