// src/pages/ExportPage.jsx
import React, { useState } from 'react';
import { requestExportDocx, requestExportPdf } from '../api/api';

export default function ExportPage() {
  const [loading, setLoading] = useState(false);

  const downloadBlob = (blob, filename) => {
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  const handleExport = async (type) => {
    setLoading(true);
    try {
      console.log('Export type:', type);
      const userId = 1;
      if (type === 'docx') {
        const res = await requestExportDocx(userId);
        downloadBlob(res.data, `report_user_${userId}.docx`);
      } else if (type === 'pdf') {
        const res = await requestExportPdf(userId);
        downloadBlob(res.data, `report_user_${userId}.pdf`);
      } else {
        console.warn('Unknown export type:', type);
      }
    } catch (err) {
      console.error('Export error:', err);
      alert('ÌååÏùº Îã§Ïö¥Î°úÎìúÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="mb-4">üì• Ï∑®Ï§Ä Í∏∞Î°ù Îã§Ïö¥Î°úÎìú</h2>
      <div className="d-flex gap-3">
        <button
          className="btn btn-outline-primary"
          disabled={loading}
          onClick={() => handleExport('docx')}
        >
          DocxÎ°ú Îã§Ïö¥Î°úÎìú
        </button>
        <button
          className="btn btn-outline-success"
          disabled={loading}
          onClick={() => handleExport('pdf')}
        >
          PDFÎ°ú Îã§Ïö¥Î°úÎìú
        </button>
      </div>
      {loading && <div className="mt-4 text-muted">ÌååÏùº ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§...</div>}
    </div>
  );
}
