import React, { useState, useEffect } from 'react';
import { uploadResume, getResumeList, getFeedback } from '../api/api';

export default function ResumePage() {
  // 업로드할 텍스트
  const [text, setText] = useState('');
  // 기존에 올라온 이력서 목록
  const [resumes, setResumes] = useState([]);

  // ★ 추가: Feedback 결과를 보관할 state
  const [editedText, setEditedText]   = useState('');
  const [feedbackText, setFeedbackText] = useState('');

  // 이력서 목록 가져오기
  const fetchResumes = async () => {
    const r = await getResumeList();
    setResumes(r.data);
  };

  useEffect(() => {
    fetchResumes();
  }, []);

  // 새 이력서 업로드
  const handleUpload = async () => {
    await uploadResume(1, text);
    setText('');
    fetchResumes();
  };

  // Feedback 요청 → alert 대신 화면에 렌더링
  const handleFeedback = async (id) => {
    const r = await getFeedback(id);
    // Response data 구조: { edited_text: "...", feedback: "..." }
    setEditedText(r.data.edited_text);
    setFeedbackText(r.data.feedback);
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Resume Management</h2>

      {/* 업로드 폼 */}
      <textarea
        className="w-full p-2 border rounded mb-2"
        value={text}
        onChange={e => setText(e.target.value)}
        rows={4}
        placeholder="이력서 내용을 입력하세요"
      />
      <br/>
      <button
        className="px-4 py-2 bg-blue-600 text-white rounded"
        onClick={handleUpload}
      >
        Upload
      </button>

      {/* 이력서 리스트 */}
      <ul className="mt-6 space-y-2">
        {resumes.map(r => (
          <li key={r.id} className="flex items-center space-x-2">
            <span className="truncate w-64">{r.original_text.slice(0, 30)}...</span>
            <button
              className="px-2 py-1 bg-green-500 text-white rounded"
              onClick={() => handleFeedback(r.id)}
            >
              Feedback
            </button>
          </li>
        ))}
      </ul>

      {/* ★ Feedback 결과 렌더링 */}
      {(editedText || feedbackText) && (
        <div className="mt-8 p-4 border rounded bg-gray-50">
          <h3 className="text-xl font-semibold mb-2">Edited Text</h3>
          <p className="whitespace-pre-wrap mb-4">{editedText}</p>

          <h3 className="text-xl font-semibold mb-2">Feedback</h3>
          <p className="whitespace-pre-wrap">{feedbackText}</p>
        </div>
      )}
    </div>
  );
}
