import React, { useState } from 'react';
import {
  requestExportDocx,
  requestExportPdf,
  downloadDocx,
  downloadPdf,
} from '../api/api';

export default function ExportPage() {
  const [loading, setLoading] = useState(false);

  const handleExport = async (type) => {
    setLoading(true);
    try {
      // 1) Report 생성 요청 → { filename, download_url } 받기
      const exportRes = type === 'docx'
        ? await requestExportDocx(1)
        : await requestExportPdf(1);

      const { filename, download_url } = exportRes.data;

      // 2) 실제 파일 다운로드 요청 (blob)
      const fileRes = await (
        type === 'docx'
          ? downloadDocx(filename)
          : downloadPdf(filename)
      );

      // 3) 브라우저 다운로드 트리거
      const blob = new Blob([fileRes.data], { type: fileRes.headers['content-type'] });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <button disabled={loading} onClick={() => handleExport('docx')}>
        DOCX로 다운로드
      </button>
      <button disabled={loading} onClick={() => handleExport('pdf')}>
        PDF로 다운로드
      </button>
      {loading && <div>파일 생성/다운로드 중…</div>}
    </div>
  );
}
