import React, { useState } from 'react';
import { generateQuestions, saveAnswer, evaluateAnswer } from '../lib/api';

export default function InterviewPage() {
  const [company, setCompany] = useState('');
  const [role, setRole] = useState('');
  const [questions, setQuestions] = useState([]);
  const [evaluation, setEvaluation] = useState(null);

  const handleGenerate = async () => {
    if (!company.trim() || !role.trim()) return;
    const res = await generateQuestions({ user_id: 1, company, role });
    setQuestions(res.data.questions);
    setEvaluation(null);
  };

  const handleSave = async (q, idx) => {
    await saveAnswer({ user_id: 1, question_id: q.id, answer: q.userAnswer });
    const updated = [...questions];
    updated[idx].answerSaved = true;
    setQuestions(updated);
  };

  const handleEvaluate = async (q) => {
    const res = await evaluateAnswer(q.answer_id, { answer: q.userAnswer });
    setEvaluation(res.data);
  };

  return (
    <div>
      <h2 className="mb-4">üé§ Mock Interview</h2>

      <div className="row g-3 mb-5">
        <div className="col-md-5">
          <input
            type="text"
            className="form-control shadow-sm"
            placeholder="Company"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
          />
        </div>
        <div className="col-md-5">
          <input
            type="text"
            className="form-control shadow-sm"
            placeholder="Role"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          />
        </div>
        <div className="col-md-2">
          <button className="btn btn-success w-100" onClick={handleGenerate}>
            Generate
          </button>
        </div>
      </div>

      <ul className="list-group mb-4 shadow-sm">
        {questions.map((q, idx) => (
          <li key={q.id} className="list-group-item">
            <p className="mb-2"><strong>Q:</strong> {q.question}</p>
            <textarea
              className="form-control mb-3"
              rows={3}
              value={q.userAnswer || ''}
              onChange={(e) => {
                const updated = [...questions];
                updated[idx].userAnswer = e.target.value;
                setQuestions(updated);
              }}
            />
            <div className="d-flex gap-2">
              <button
                className="btn btn-outline-primary btn-sm"
                onClick={() => handleSave(q, idx)}
              >
                {q.answerSaved ? 'Saved' : 'Save'}
              </button>
              <button
                className="btn btn-outline-info btn-sm"
                onClick={() => handleEvaluate(q)}
              >
                Evaluate
              </button>
            </div>
          </li>
        ))}
      </ul>

      {evaluation && (
        <div className="card shadow-sm">
          <div className="card-body">
            <h4 className="card-title">üìù Evaluation</h4>
            <p><strong>Score:</strong> {evaluation.score}</p>
            <p><strong>Feedback:</strong> {evaluation.feedback}</p>
          </div>
        </div>
      )}
    </div>
  );
}
