// src/pages/InterviewPage.jsx
import React, { useState } from 'react'
import {
  generateQuestions,    // POST /interviews/questions
  saveAnswer,           // POST /interviews/answers
  evaluateAnswer        // POST /interviews/evaluate/{answer_id},payload={answer_text}
} from '../api/api'

export default function InterviewPage() {
  const [company, setCompany] = useState('')
  const [role, setRole] = useState('')
  const [questions, setQuestions] = useState([])         // [{ id, text }, ...]
  const [answers, setAnswers] = useState({})            // { 0: '답변1', ... }
  const [answerIds, setAnswerIds] = useState({})        // { 0: 123, ... }
  const [evaluations, setEvaluations] = useState({})    // { 0: {score,feedback}, ... }

  // 1) 질문 생성
  const handleGenerate = async () => {
    if (!company.trim() || !role.trim()) {
      return alert('Company와 Role을 모두 입력하세요.')
    }

    try {
      const res = await generateQuestions({ user_id: 1, company, role })
      console.log('▶ payload:', { company, role })
      console.log('⬇ questions:', res.data.questions)
      setQuestions(res.data.questions || [])
      // 이전 답변·평가 초기화
      setAnswers({})
      setAnswerIds({})
      setEvaluations({})
    } catch (err) {
      console.error('❌ 질문 생성 실패', err)
      alert('질문 생성에 실패했습니다.')
    }
  }

  // 2) 답변 저장
  const handleSave = async (idx) => {
    const q = questions[idx]
    const text = answers[idx]?.trim()
    if (!q || !text) {
      return alert('먼저 질문을 생성하고, 답변을 입력하세요.')
    }

    const payload = {
      question_id: idx+1,
      answer_text: text
    };
    console.log('>> saveAnswer payload:', payload);
    try {
      const res = await saveAnswer(payload);
      setAnswerIds(prev => ({ ...prev, [idx]: res.data.id }))
      alert(`답변 저장됨 (answer_id=${res.data.id})`)
    } catch (err) {
      console.error('❌ 답변 저장 실패', err)
      alert('답변 저장에 실패했습니다.')
    }
  }

  // 3) 답변 평가
  const handleEval = async (idx) => {
    const aid = answerIds[idx]
    if (!aid) {
      return alert('먼저 Save Answer를 눌러주세요.')
    }

    const text=(answers[idx] || '').trim()
    if (!text) {
      return alert('평가할 답변을 입력하세요')
    }
    try {
      const res = await evaluateAnswer(aid, { answer_text: text })
      setEvaluations(prev => ({ ...prev, [idx]: res.data }))
    } catch (err) {
      console.error('❌ 평가 실패', err)
      alert('평가에 실패했습니다.')
    }
  }

  return (
    <div style={{ padding: 24, maxWidth: 800, margin: '0 auto' }}>
      <h2>Interview Preparation</h2>

      <div style={{ marginBottom: 16 }}>
        <label>
          Company:&nbsp;
          <input
            value={company}
            onChange={e => setCompany(e.target.value)}
            placeholder="지원할 회사명을 입력하세요"
            style={{ width: 200 }}
          />
        </label>
        &nbsp;&nbsp;
        <label>
          Role:&nbsp;
          <input
            value={role}
            onChange={e => setRole(e.target.value)}
            placeholder="지원할 직무를 입력하세요"
            style={{ width: 200 }}
          />
        </label>
        &nbsp;
        <button onClick={handleGenerate}>Generate Questions</button>
      </div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {questions.length === 0 && (
          <li style={{ color: '#666' }}>
            아직 생성된 질문이 없습니다. “Generate Questions”를 눌러 보세요.
          </li>
        )}

        {questions.map((q, i) => {
          // q가 string일 수도, object일 수도 있으니 유연하게 처리
          const text =
            typeof q === 'string'
              ? q
              : q.text ?? q.question ?? JSON.stringify(q)

          return (
            <li key={q.id ?? i} style={{ marginBottom: 32 }}>
              <strong>Q{i + 1}.</strong> {text}
              <br />

              <textarea
                rows={3}
                cols={80}
                value={answers[i] || ''}
                onChange={e =>
                  setAnswers(prev => ({ ...prev, [i]: e.target.value }))
                }
                placeholder="답변을 입력하세요"
                style={{ marginTop: 8 }}
              />
              <br />

              <button onClick={() => handleSave(i)}>Save Answer</button>
              &nbsp;
              <button onClick={() => handleEval(i)}>Evaluate Answer</button>

              {evaluations[i] && (
                <div style={{ marginTop: 8, color: 'green' }}>
                  <div>
                    <strong>Score:</strong> {evaluations[i].score}
                  </div>
                  <div>
                    <strong>Feedback:</strong> {evaluations[i].feedback}
                  </div>
                </div>
              )}
            </li>
          )
        })}
      </ul>
    </div>
  )
}
