// frontend/src/pages/ExportPage.jsx
import React, { useState, useEffect } from 'react'
import { getResumeList, downloadDocx, downloadPdf } from '../api/api'

export default function ExportPage() {
  const [resumes, setResumes] = useState([])
  const [error, setError]     = useState(null)

  // 1) 컴포넌트 마운트 시 이력서 목록 로드
  useEffect(() => {
    (async () => {
      try {
        const res = await getResumeList()
        setResumes(res.data)
      } catch (e) {
        setError(e.message)
      }
    })()
  }, [])

  // Blob 을 파일로 저장하는 헬퍼
  const saveBlob = (blob, filename) => {
    const url = window.URL.createObjectURL(blob)
    const a   = document.createElement('a')
    a.href     = url
    a.download = filename
    document.body.appendChild(a)
    a.click()
    a.remove()
    window.URL.revokeObjectURL(url)
  }

  // DOCX 다운로드 핸들러
  const handleDocx = async (id) => {
    try {
      const res = await downloadDocx(id)
      saveBlob(res.data, `resume_${id}.docx`)
    } catch (e) {
      console.error(e)
      alert('DOCX 다운로드 실패: ' + e.message)
    }
  }

  // PDF 다운로드 핸들러
  const handlePdf = async (id) => {
    try {
      const res = await downloadPdf(id)
      saveBlob(res.data, `resume_${id}.pdf`)
    } catch (e) {
      console.error(e)
      alert('PDF 다운로드 실패: ' + e.message)
    }
  }

  if (error) {
    return <p className="p-4 text-red-600">에러 발생: {error}</p>
  }

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Export Documents</h2>
      <ul className="space-y-4">
        {resumes.map(r => (
          <li
            key={r.id}
            className="border p-4 rounded flex flex-col md:flex-row md:justify-between items-start md:items-center"
          >
            {/* 원문 표시 */}
            <div className="whitespace-pre-wrap mb-2 md:mb-0">{r.original_text}</div>

            {/* 다운로드 버튼 */}
            <div className="space-x-2">
              <button
                className="px-3 py-1 bg-blue-600 text-white rounded"
                onClick={() => handleDocx(r.id)}
              >
                Download DOCX
              </button>
              <button
                className="px-3 py-1 bg-green-600 text-white rounded"
                onClick={() => handlePdf(r.id)}
              >
                Download PDF
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  )
}
