// frontend/src/api/api.js
import axios from 'axios'

export const api = axios.create({
  baseURL: 'http://localhost:8000'
})

// Resume 기능
export const getResumeList = () => api.get('/resumes')
export const uploadResume  = (text) => api.post('/resumes', { text })
export const getFeedback   = (id)   => api.post(`/resumes/${id}/feedback`)

// Export 기능 추가
export const downloadDocx = (id) =>
  api.get(`/exporter/${id}/docx`, { responseType: 'blob' })

export const downloadPdf = (id) =>
  api.get(`/exporter/${id}/pdf`,  { responseType: 'blob' })
