import React, { useState, useEffect } from 'react';
import { uploadResume, getResumeList, getFeedback } from '../api/api';

export default function ResumePage() {
  const [text, setText] = useState('');
  const [resumes, setResumes] = useState([]);

  const fetchResumes = async () => {
    const r = await getResumeList();
    setResumes(r.data);
  };

  useEffect(() => { fetchResumes(); }, []);

  const handleUpload = async () => {
    await uploadResume(1, text);
    setText('');
    fetchResumes();
  };

  const handleFeedback = async (id) => {
    const r = await getFeedback(id);
    alert(JSON.stringify(r.data, null, 2));
  };

  return (
    <div>
      <h2>Resume Management</h2>
      <textarea value={text} onChange={e => setText(e.target.value)} rows={4} cols={50}/>
      <br/>
      <button onClick={handleUpload}>Upload</button>
      <ul>
        {resumes.map(r => (
          <li key={r.id}>
            {r.original_text.slice(0, 30)}... 
            <button onClick={() => handleFeedback(r.id)}>Feedback</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
