import React, { useState, useEffect } from 'react';
import { uploadResume, getResumeList, getFeedback } from '../lib/api';

export default function ResumePage() {
  const [text, setText] = useState('');
  const [resumes, setResumes] = useState([]);
  const [editedText, setEditedText] = useState('');
  const [feedbackText, setFeedbackText] = useState('');

  useEffect(() => {
    fetchResumes();
  }, []);

  const fetchResumes = async () => {
    try {
      const res = await getResumeList(1);
      setResumes(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleUpload = async () => {
    if (!text.trim()) return;
    try {
      await uploadResume(1, text);
      setText('');
      fetchResumes();
    } catch (err) {
      console.error(err);
    }
  };

  const handleFeedback = async (id) => {
    try {
      const res = await getFeedback(id);
      setEditedText(res.data.edited_text);
      setFeedbackText(res.data.feedback);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h2 className="mb-4">üìù Resume Builder</h2>

      <div className="mb-3">
        <textarea
