import React, { useState } from 'react';
import { requestExportDocx, requestExportPdf } from '../api/api';

export default function ExportPage() {
  const [loading, setLoading] = useState(false);

  const handleExport = async (type) => {
    setLoading(true);
    try {
      const res =
        type === 'docx'
          ? await requestExportDocx(1)
          : await requestExportPdf(1);

      const blob = new Blob([res.data], {
        type: res.headers['content-type'],
      });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `resume.${type}`;
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  return (
    <div>
      <h2 className="mb-4">üì• Îã§Ïö¥Î°úÎìú</h2>
      <div className="d-flex gap-3">
        <button
          className="btn btn-outline-primary"
          disabled={loading}
          onClick={() => handleExport('docx')}
        >
          Export to DOCX
        </button>
        <button
          className="btn btn-outline-success"
          disabled={loading}
          onClick={() => handleExport('pdf')}
        >
          Export to PDF
        </button>
      </div>
      {loading && (
        <div className="mt-4 text-muted">ÌååÏùº ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§...</div>
      )}
    </div>
  );
}
