// src/pages/ResumePage.jsx
import React, { useState, useEffect } from 'react';
import { uploadResume, getResumeList, getFeedback } from '../api/api';

export default function ResumePage() {
  const [text, setText] = useState('');
  const [resumes, setResumes] = useState([]);
  const [editedText, setEditedText] = useState('');
  const [feedbackText, setFeedbackText] = useState('');

  useEffect(() => {
    fetchResumes();
  }, []);

  const fetchResumes = async () => {
    try {
      const res = await getResumeList();
      setResumes(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleUpload = async () => {
    if (!text.trim()) return;
    try {
      await uploadResume(1, text);
      setText('');
      fetchResumes();
    } catch (err) {
      console.error(err);
    }
  };

  const handleFeedback = async (id) => {
    try {
      const res = await getFeedback(id);
      setEditedText(res.data.edited_text);
      setFeedbackText(res.data.feedback);
    } catch (err) {
      console.error(err);
    }
  };

  // Ïù¥Î†•ÏÑú ÌÖçÏä§Ìä∏ÏóêÏÑú Ï≤´ Îëê Ï§ÑÎßå Ï∂îÏ∂ú
  const getPreview = (fullText) => {
    const lines = fullText.split('\n').filter(l => l.trim() !== '');
    const preview = lines.slice(0, 2).join(' ');
    return preview + (lines.length > 2 ? '...' : '');
  };

  return (
    <div>
      <h2 className="mb-4">üìù ÏûëÏÑ±Ìïú Ïù¥Î†•ÏÑú ÏûÖÎ†•</h2>

      <div className="mb-3">
        <textarea
          className="form-control shadow-sm"
          placeholder="Ïù¥Î†•ÏÑú ÎÇ¥Ïö©ÏùÑ Ïó¨Í∏∞Ïóê Î∂ôÏó¨ÎÑ£ÏúºÏÑ∏Ïöî!"
          rows={6}
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      </div>
      <button className="btn btn-primary mb-5" onClick={handleUpload}>
        Ïù¥Î†•ÏÑú Ï≤®ÏÇ≠
      </button>

      {/* ‚òÖ ÌîºÎìúÎ∞± Ïπ¥Îìú Î®ºÏ†Ä */}
      {(editedText || feedbackText) && (
        <div className="card shadow-sm mb-5">
          <div className="card-body">
            <h4 className="card-title">‚úèÔ∏è Edited Text</h4>
            <div
              className="card-text"
              style={{
                whiteSpace: 'pre-wrap',   // ÏõêÎ¨∏ Ï§ÑÎ∞îÍøà Î≥¥Ï°¥
                overflowX: 'auto'         // Í∏¥ Ï§ÑÏùÄ Í∞ÄÎ°ú Ïä§ÌÅ¨Î°§
              }}
            >
              {editedText}
            </div>
            <h4 className="card-title mt-4">üí° Feedback</h4>
            <p className="card-text">{feedbackText}</p>
          </div>
        </div>
      )}

      <h3 className="mb-3">üìÇ Ïù¥Î†•ÏÑú ÌûàÏä§ÌÜ†Î¶¨</h3>
      <ul className="list-group mb-4 shadow-sm">
        {resumes.map((res) => (
          <li
            key={res.id}
            className="list-group-item d-flex justify-content-between align-items-start"
          >
            <div>
              <div><strong>ID:</strong> {res.id}</div>
              <div className="text-muted small">
                {getPreview(res.text || res.original_text || '')}
              </div>
            </div>
            <button
              className="btn btn-outline-secondary btn-sm"
              onClick={() => handleFeedback(res.id)}
            >
              ÌîºÎìúÎ∞± Î≥¥Í∏∞
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
