import React, { useState, useEffect } from 'react'
import axios from 'axios'

export default function DashboardPage() {
  const [items, setItems] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError]     = useState(null)

  useEffect(() => {
    const fetchAll = async () => {
      try {
        // 1) 이력서 목록 가져오기
        const res = await axios.get('http://localhost:8000/resumes')
        const resumes = res.data  // [{ id, original_text }, ...]

        // 2) 각 이력서에 대해 feedback 호출
        const withFeedback = await Promise.all(
          resumes.map(async (r) => {
            const fb = await axios.post(
              `http://localhost:8000/resumes/${r.id}/feedback`
            )
            return {
              id: r.id,
              original: r.original_text,
              edited: fb.data.edited_text,
              feedback: fb.data.feedback
            }
          })
        )

        setItems(withFeedback)
      } catch (err) {
        console.error(err)
        setError(err.message)
      } finally {
        setLoading(false)
      }
    }

    fetchAll()
  }, [])

  if (loading) return <p>로딩 중...</p>
  if (error)   return <p>에러 발생: {error}</p>

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Dashboard</h2>
      <table className="w-full table-auto border-collapse">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-2 py-1">ID</th>
            <th className="border px-2 py-1">원문</th>
            <th className="border px-2 py-1">수정본</th>
            <th className="border px-2 py-1">피드백</th>
          </tr>
        </thead>
        <tbody>
          {items.map((it) => (
            <tr key={it.id} className="hover:bg-gray-50">
              <td className="border px-2 py-1">{it.id}</td>
              <td className="border px-2 py-1 whitespace-pre-wrap">
                {it.original}
              </td>
              <td className="border px-2 py-1 whitespace-pre-wrap">
                {it.edited}
              </td>
              <td className="border px-2 py-1 whitespace-pre-wrap">
                {it.feedback}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
